mapper.select:
select cb.class_name , cb.class_seq , cb.class_content , cb.in_id , cb.in_dtm from classBoard cb left join contain c on cb.class_seq = c.class_seq where word_seq in
#{arr}
and c.class_seq != #{class_seq} and c.contain_cnt > 1 order by contain_cnt desc

mapper.insert:
insert into classBoard
(
	  class_name
	, class_content
	, in_id
	, in_dtm
	, up_id
	, up_dtm
) values
(
	  #{class_name}
  , #{class_content}
  , #{in_id}
  , now()
  , #{up_id}
  , now()
)
